import React from "react";

interface PostProps {
    idPhoto: number;
}

const listephoto = [
    { id: 1, content: "Photo 1", tags: ["tacle", "odoo"], description: "Description de la photo 1" },
    { id: 2, content: "Photo 2", tags: ["glissade", "prof"], description: "Description de la photo 2" },
    { id: 3, content: "Photo 3", tags: ["inazuma", "tacledelamort"], description: "Description de la photo 3" },
];

const Post: React.FC<PostProps> = ({ idPhoto }) => {
    const photo = listephoto.find(p => p.id === idPhoto);

    if (!photo) {
        return <div>Photo not found</div>;
    }

    return (
        <div style={{ border: "1px solid #ccc", padding: "16px", borderRadius: "8px", maxWidth: "400px", margin: "16px auto" }}>
            <h2>{photo.content}</h2>
            <img src={require('../../images/meme.gif')} alt="Photo" style={{ width: "100%", height: "auto" }} />
            <p>{photo.description}</p>
            <div style={{ marginTop: "8px" }}>
                {photo.tags.map((tag, index) => (
                    <span key={index} style={{ marginRight: "8px", padding: "4px 8px", background: "#eee", borderRadius: "4px" }}>
                        #{tag}
                    </span>
                ))}
            </div>
            <div style={{ display: "flex", justifyContent: "space-between", marginTop: "8px" }}>
                <button onClick={() => console.log("Commenté !")}>
                    Commenter <span>0</span>
                </button>
            </div>
        </div>
    );
};

export default Post;
