import React, { useState } from "react";

interface UserProfileProps {
  setPage: any;
}

const Profil: React.FC<UserProfileProps> = ({ setPage }) => {
  // Définir les états pour chaque champ
  const [isNomPublic, setIsNomPublic] = useState<boolean>(false);
  const [isPrenomPublic, setIsPrenomPublic] = useState<boolean>(false);
  const [isEmailPublic, setIsEmailPublic] = useState<boolean>(false);
  const [isTelephonePublic, setIsTelephonePublic] = useState<boolean>(false);

  // Valeurs par défaut pour les données
  const nom = "xxxx ";
  const prenom = "xxxx ";
  const email = "xxxxx@gmail.com";
  const telephone = "+33 6 XX XX XX XX";

  return (
    <div className="user-profile">
      <h1>a's Profile</h1>
      <button>Modifier</button>
      <div>
        <p>
          Nom: {nom}
          <button onClick={() => setIsNomPublic(!isNomPublic)}>
            {isNomPublic ? "Public" : "Privé"}
          </button>
        </p>
        <p>
          Prénom: {prenom}
          <button onClick={() => setIsPrenomPublic(!isPrenomPublic)}>
            {isPrenomPublic ? "Public" : "Privé"}
          </button>
        </p>
        <p>
          Email: {email}
          <button onClick={() => setIsEmailPublic(!isEmailPublic)}>
            {isEmailPublic ? "Public" : "Privé"}
          </button>
        </p>
        <p>
          Téléphone: {telephone}
          <button onClick={() => setIsTelephonePublic(!isTelephonePublic)}>
            {isTelephonePublic ? "Public" : "Privé"}
          </button>
        </p>
      </div>
      <button onClick={() => setPage(1)}>Fil de photo</button>
    </div>
  );
};

export default Profil;
