import React, { useState } from "react";

interface UserProfileProps {
  setPage: any;
}

const Profil: React.FC<UserProfileProps> = ({ setPage }) => {
  // Définir les états pour chaque champ
  const [isNomOn, setIsNomOn] = useState<boolean>(false);
  const [isPrenomOn, setIsPrenomOn] = useState<boolean>(false);
  const [isEmailOn, setIsEmailOn] = useState<boolean>(false);
  const [isTelephoneOn, setIsTelephoneOn] = useState<boolean>(false);

  // Valeurs par défaut pour les données
  const nom = "xxxx";
  const prenom = "xxxx";
  const email = "xxxxx@gmail.com";
  const telephone = "+33 6 XX XX XX XX";

  return (
    <div className="user-profile">
      <h1>a's Profile</h1>
      <button>Modifier</button>
      <div>
        <p>
          Nom: {nom}
          <button onClick={() => setIsNomOn(!isNomOn)}>
            {isNomOn ? "On" : "Off"}
          </button>
        </p>
        <p>
          Prénom: {prenom}
          <button onClick={() => setIsPrenomOn(!isPrenomOn)}>
            {isPrenomOn ? "On" : "Off"}
          </button>
        </p>
        <p>
          Email: {email}
          <button onClick={() => setIsEmailOn(!isEmailOn)}>
            {isEmailOn ? "On" : "Off"}
          </button>
        </p>
        <p>
          Téléphone: {telephone}
          <button onClick={() => setIsTelephoneOn(!isTelephoneOn)}>
            {isTelephoneOn ? "On" : "Off"}
          </button>
        </p>
      </div>
      <button onClick={() => setPage(1)}>Fil de photo</button>
    </div>
  );
};

export default Profil;
