import { useState, useEffect } from 'react'
import axios from 'axios'
import './App.css'
import Inscription from './Component/PagedeConnexion/Inscription'
import Fil from './Component/Filphoto/fil'
import Profil from './Component/pageutilisateur/profil'
import HeaderInterne from './Component/Header/headerinterne'
import Uploadphoto from './Component/uploadphoto/uploadphoto'
import Connexion from './Component/PagedeConnexion/Connexion'
import HeaderPublic from './Component/Header/headerpublic'


function App() {
  const [page, setPage] = useState(1);
  const [isConnected, setIsConnected] = useState(false);

  useState(() => {
    const token = localStorage.getItem('authToken');
    if (!token) {
      setIsConnected(false);
    } else {
      axios.get('http://localhost:5000/GET/verify-token', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      })
        .then(response => {
          if (response.data.message === 'Token is valid') {
            setIsConnected(true);
          } else {
            setIsConnected(false);
          }
        })
        .catch(() => {
          setIsConnected(false);
        });
    }
    console.log('isConnected:', isConnected);
  }, );

  return (
    <>
    <div>

    {isConnected === true && (
      <HeaderInterne 
      setPage={setPage}/>
    )}
    {isConnected === false && (
      <HeaderPublic 
      setPage={setPage}/>
    )}
    </div>
    <div className='page-content'>
      {page === 1 && (
        <Fil
          setPage={setPage} />
      )}
      {page === 2 && (
        <Profil
          setPage={setPage} />
      )}
      {page === 3 && (
        <Inscription
          setPage={setPage} />
      )}
      {page === 4 && (
        <Uploadphoto 
          setPage={setPage} />
      )}
      {page === 5 && (
        <Connexion
          setPage={setPage} />
      )}
    </div>
    </>
  );
}

export default App;
