import React, { useState } from 'react';
import './Connexion.css';
import axios from 'axios';

interface ConnexionProps {
  setPage: (page: number) => void;
}

const Connexion: React.FC<ConnexionProps> = ({ setPage }) => {
  const [email, setEmail] = useState<string>('');
  const [password, setPassword] = useState<string>('');

  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    console.log('Connexion en cours...');

    axios.post('http://localhost:5000/GET/connexion', {
      mail: email,
      mdp: password
    }).then(response => {
      console.log(response.data);
      setPage(1);
    }).catch(error => {
      console.error('There was an error!', error);
    });
  }

  return (
    <div className="connexion-container">
      <h2>Connexion</h2>
      <form onSubmit={handleLogin} className="connexion-form">
        <div className="input-container">
          <label htmlFor="email">Adresse e-mail</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            className="input-field"
          />
        </div>
        <div className="input-container">
          <label htmlFor="password">Mot de passe</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            className="input-field"
          />
        </div>
        <button type="submit" className="connexion-button">Connexion</button>
      </form>
    </div>
  );
}

export default Connexion;
