import React, { useState } from 'react';
import './Connexion.css';
import axios from 'axios';

interface ConnexionProps {
  setPage: (page: number) => void;
  setIsConnected: (isConnected: boolean) => void;
}

const Connexion: React.FC<ConnexionProps> = ({ setPage, setIsConnected }) => {
  const [email, setEmail] = useState<string>('');
  const [password, setPassword] = useState<string>('');

  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    console.log('Connexion en cours...');

    axios
      .get(
        `http://localhost:5000/GET/connexion?mail=${encodeURIComponent(
          email,
        )}&mdp=${encodeURIComponent(password)}`,
      )
      .then((response) => {
    console.log('Success:', response.data);
    const token = response.data;
    localStorage.setItem('authToken', token);
    axios.get('http://localhost:5000/GET/verify-token', {
      params: {
        token: token
      }
    });
    console.log('reponse:', response.data);
    if (response.data.message === 'Token is valid') {
      setIsConnected(true);
    } else {
      setIsConnected(false);
    }
    console.log('cyka blayt')
    setPage(1);
  })
      .catch((error) => {
        if (error.response && error.response.status === 401) {
          console.error('Mot de passe incorrect');
        } else {
          console.error('Erreur lors de la requête :', error);
        }
      });
  }
  

  return (
    <div className="connexion-container">
      <h2>Connexion</h2>
      <form onSubmit={handleLogin} className="connexion-form">
        <div className="input-container">
          <label htmlFor="email">Adresse e-mail</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            className="input-field"
          />
        </div>
        <div className="input-container">
          <label htmlFor="password">Mot de passe</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            className="input-field"
          />
        </div>
        <button type="submit" className="connexion-button">Connexion</button>
      </form>
    </div>
  );
}

export default Connexion;
