import React, { useState } from "react";

interface UserProfileProps {
  setPage: any;
}

const Profil: React.FC<UserProfileProps> = ({ setPage }) => {
  // Définir les états pour chaque champ avec les valeurs par défaut
  const [nomVisible, setNomVisible] = useState<boolean>(true);
  const [prenomVisible, setPrenomVisible] = useState<boolean>(true);
  const [emailVisible, setEmailVisible] = useState<boolean>(true);
  const [telephoneVisible, setTelephoneVisible] = useState<boolean>(true);

  // Valeurs par défaut pour les données
  const nom = "xxxx";
  const prenom = "xxxx";
  const email = "xxxxx@gmail.com";
  const telephone = "+33 6 XX XX XX XX";

  return (
    <div className="user-profile">
      <h1>a's Profile</h1>
      <button>Modifier</button>
      <div>
        <p>
          Nom: {nomVisible ? nom : "****"}
          <button onClick={() => setNomVisible(!nomVisible)}>
            {nomVisible ? "Masquer" : "Afficher"}
          </button>
        </p>
        <p>
          Prénom: {prenomVisible ? prenom : "****"}
          <button onClick={() => setPrenomVisible(!prenomVisible)}>
            {prenomVisible ? "Masquer" : "Afficher"}
          </button>
        </p>
        <p>
          Email: {emailVisible ? email : "****"}
          <button onClick={() => setEmailVisible(!emailVisible)}>
            {emailVisible ? "Masquer" : "Afficher"}
          </button>
        </p>
        <p>
          Téléphone: {telephoneVisible ? telephone : "****"}
          <button onClick={() => setTelephoneVisible(!telephoneVisible)}>
            {telephoneVisible ? "Masquer" : "Afficher"}
          </button>
        </p>
      </div>
      <button onClick={() => setPage(1)}>Fil de photo</button>
    </div>
  );
};

export default Profil;
