import React, { useState } from "react";

interface UserProfileProps {
  setPage: any;
}

const Profil: React.FC<UserProfileProps> = ({ setPage }) => {
  // Définir les états pour chaque champ
  const [nom, setNom] = useState<string>("xxxx");
  const [prenom, setPrenom] = useState<string>("xxxx");
  const [email, setEmail] = useState<string>("xxxxx@gmail.com");
  const [telephone, setTelephone] = useState<string>("+33 6 XX XX XX XX");

  // Fonction pour échanger deux valeurs
  const handleSwap = (setterA: React.Dispatch<React.SetStateAction<string>>, valueA: string, setterB: React.Dispatch<React.SetStateAction<string>>, valueB: string) => {
    setterA(valueB);
    setterB(valueA);
  };

  return (
    <div className="user-profile">
      <h1>a's Profile</h1>
      <button>Modifier</button>
      <div>
        <p>
          Nom: {nom} <button onClick={() => handleSwap(setNom, nom, setPrenom, prenom)}>Swap</button>
        </p>
        <p>
          Prénom: {prenom} <button onClick={() => handleSwap(setPrenom, prenom, setNom, nom)}>Swap</button>
        </p>
        <p>
          Email: {email} <button onClick={() => handleSwap(setEmail, email, setTelephone, telephone)}>Swap</button>
        </p>
        <p>
          Téléphone: {telephone} <button onClick={() => handleSwap(setTelephone, telephone, setEmail, email)}>Swap</button>
        </p>
      </div>
      <button onClick={() => setPage(1)}>Fil de photo</button>
    </div>
  );
};

export default Profil;
